#include<stdio.h> #include<string.h> #include<algorithm> #include<iostream> using namespace std; bool m[201][201];  int main() { 	int citynum, roadnum; 	while(cin >> citynum && citynum != 0) { 		cin >> roadnum; 		for (int i = 0; i < citynum; i++) { 			for (int j = 0; j < citynum; j++) { 				m[i][j] = false; 			}  		} 		while(roadnum--) { 			int a, b; 			cin >> a >> b; 			m[a][b] = true; 		}  		for (int i = 0; i < citynum; i++) { 			for (int j = 0; j < citynum; j++) { 				if(m[i][j]) { 				 					for (int k = 0; k < citynum; k++) { 						if (m[j][k]) { 						 							m[i][k] = true; 							 						}  					}  				}  			}  		}  		 		if (m[0][citynum - 1]) cout << "I can post the letter" << endl; 		else cout << "I can't post the letter" << endl;    	} 	system("pause"); 	 return 0; } 